import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("https://www.reddit.com/r/reactjs.json")
      .then((response) => response.json())
      .then((json) => setData(json.data.children));
  }, []);

  const renderCards = () => {
    return data.map((post) => {
      const { title, selftext_html, url, score } = post.data;
      const parser = new DOMParser();
      const plainText = parser.parseFromString(
        selftext_html,
        "text/html"
      ).documentElement.textContent;

      return (
        <div className="card" key={post.data.id}>
          <div className="card-body">
            <h5 className="card-title">{title}</h5>
            <p className="card-text">{plainText}</p>
            <a href={url} className="card-link">
              {url}
            </a>
            <p className="card-score">Score: {score}</p>
          </div>
        </div>
      );
    });
  };

  return (
    <div className="container">
      <div className="row">{renderCards()}</div>
    </div>
  );
}

export default App;
